namespace RK\InfoModule\Container\Base;

use Zikula\Bundle\HookBundle\AbstractHookContainer as ZikulaHookContainer;

use Zikula\Bundle\HookBundle\Bundle\SubscriberBundle;

/**
 * Base class for hook container methods.
 */
abstract class AbstractHookContainer extends ZikulaHookContainer
{
    /**
     * Define the hook bundles supported by this module.
     *
     * @return void
     */
    protected function setupHookBundles()
    {
        $bundle = new SubscriberBundle('RKInfoModule', 'subscriber.rkinfomodule..ui_hooks.informations', 'ui_hooks', $this->__('rkinfomodule. Informations Display Hooks'));
        
        // Display hook for view/display templates.
        $bundle->addEvent('display_view', 'rkinfomodule.ui_hooks.informations.display_view');
        // Display hook for create/edit forms.
        $bundle->addEvent('form_edit', 'rkinfomodule.ui_hooks.informations.form_edit');
        // Display hook for delete dialogues.
        $bundle->addEvent('form_delete', 'rkinfomodule.ui_hooks.informations.form_delete');
        // Validate input from an ui create/edit form.
        $bundle->addEvent('validate_edit', 'rkinfomodule.ui_hooks.informations.validate_edit');
        // Validate input from an ui delete form.
        $bundle->addEvent('validate_delete', 'rkinfomodule.ui_hooks.informations.validate_delete');
        // Perform the final update actions for a ui create/edit form.
        $bundle->addEvent('process_edit', 'rkinfomodule.ui_hooks.informations.process_edit');
        // Perform the final delete actions for a ui form.
        $bundle->addEvent('process_delete', 'rkinfomodule.ui_hooks.informations.process_delete');
        $this->registerHookSubscriberBundle($bundle);
        
        $bundle = new SubscriberBundle('RKInfoModule', 'subscriber.rkinfomodule..filter_hooks.informations', 'filter_hooks', $this->__('rkinfomodule. Informations Filter Hooks'));
        // A filter applied to the given area.
        $bundle->addEvent('filter', 'rkinfomodule.filter_hooks.informations.filter');
        $this->registerHookSubscriberBundle($bundle);
        
        
        
    }
}
